define([
    'jquery',
    'core/ajax',
], function($, ajax) {

    function init() {

        $("#et_send_rand_email_btn").on('click', function(event) {

            $('#et_send_rand_emails .alert-light').removeClass('d-none');
            $('#et_send_rand_emails .alert-success').addClass('d-none');
            $('#et_send_rand_emails .alert-danger').addClass('d-none');

            /**
             * Promise lists.
             */
            var promises = ajax.call([{
                methodname: "local_emailtrigger_send_email_to_users",
                args: {},
            }]);
    
            promises[0]
                .done(function(response) {
                    if (response.success) {
                        // $('#et_send_rand_emails .alert-success').css('display', 'block !important');
                        $('#et_send_rand_emails .alert-success').removeClass('d-none');
                    } else {
                        $('#et_send_rand_emails .alert-danger').removeClass('d-none');
                    } 
                })
                .fail(function(ex) {
                    $('#et_send_rand_emails .alert-danger').removeClass('d-none');
                });
        });

    }


    return {
        init: init
    };


});
